<?php	if( empty( $before ) )		$before = '<ul id="breadcrumb" class="breadcrumb">';	if( empty( $separator ) )		$separator = '';	if( empty( $after ) )		$after = '</ul>';		// Start	echo $before;?>	<li><a href="<?php echo home_url() ?>">Home</a><?php echo $separator ?></li>		<?php if( is_category() ) { 		$cat_obj = get_queried_object();		echo "<li>".get_category_parents( $cat_obj->term_id, true, "$separator</li><li>" );		echo '</li>';	}		elseif ( is_single() ) { ?>		<li><?php the_category( "$separator</li><li>" ) ?>			<?php echo $separator ?></li><li>			<?php the_title(); ?>		</li>	<?php } 	elseif( is_tag() ){ single_tag_title(); }	elseif( is_day() ){ echo "<li>Archive for "; the_time('F jS, Y'); echo '</li>'; }	elseif( is_month() ){ echo "<li>Archive for "; the_time('F, Y'); echo '</li>'; }	elseif( is_year() ){ echo "<li>Archive for "; the_time('Y'); echo '</li>'; }	elseif( is_author() ){ echo "<li>Author Archive"; echo '</li>'; }	elseif( is_search() ){ echo"<li>Search Results"; echo'</li>'; }	echo $after;